        /* Custom app styles */
        body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; }
        table { width: 100%; border-collapse: collapse; font-size: 0.75rem; }
