        function applyDateToAll() {
            appState.activeTests.forEach(test => test.date = appState.date);
            // Fix date display by using the actual date string instead of Date object conversion
            const dateObj = new Date(appState.date + 'T00:00:00');
            appState.successMessage = `All test dates updated to ${dateObj.toLocaleDateString()}`;
            renderApp();
            setTimeout(() => { appState.successMessage = null; renderApp(); }, 3000);
        }

        function handleDateChange(input) {
            let dateValue = input.value.trim();

            // If empty, use today's date
            if (!dateValue) {
                const today = new Date();
                const todayFormatted = formatDateForDisplay(today.toISOString().split('T')[0]);
                input.value = todayFormatted;
                appState.date = today.toISOString().split('T')[0];
                renderApp();
                return;
            }

            // Try to parse and expand different date formats
            let parsedDate = null;
            let isoDate = null;

            // Handle various shortcut formats
            if (/^\d{1,2}\/\d{1,2}\/\d{2}$/.test(dateValue)) {
                // Format: M/D/YY or MM/DD/YY (e.g., "7/3/25" or "12/25/25")
                const parts = dateValue.split('/');
                const month = parts[0].padStart(2, '0');
                const day = parts[1].padStart(2, '0');
                let year = parts[2];

                // Convert 2-digit year to 4-digit (assume 20xx for years 00-99)
                if (year.length === 2) {
                    const currentYear = new Date().getFullYear();
                    const currentCentury = Math.floor(currentYear / 100) * 100;
                    year = currentCentury + parseInt(year);
                }

                isoDate = `${year}-${month}-${day}`;
                parsedDate = new Date(isoDate + 'T00:00:00');
            }
            else if (/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(dateValue)) {
                // Format: M/D/YYYY or MM/DD/YYYY (e.g., "7/3/2025" or "12/25/2025")
                const parts = dateValue.split('/');
                const month = parts[0].padStart(2, '0');
                const day = parts[1].padStart(2, '0');
                const year = parts[2];

                isoDate = `${year}-${month}-${day}`;
                parsedDate = new Date(isoDate + 'T00:00:00');
            }
            else if (/^\d{1,2}\/\d{1,2}$/.test(dateValue)) {
                // Format: M/D or MM/DD (assume current year)
                const parts = dateValue.split('/');
                const month = parts[0].padStart(2, '0');
                const day = parts[1].padStart(2, '0');
                const year = new Date().getFullYear();

                isoDate = `${year}-${month}-${day}`;
                parsedDate = new Date(isoDate + 'T00:00:00');
            }
            else if (/^\d{4}-\d{2}-\d{2}$/.test(dateValue)) {
                // Format: YYYY-MM-DD (ISO format)
                isoDate = dateValue;
                parsedDate = new Date(dateValue + 'T00:00:00');
            }

            // Validate the parsed date
            if (parsedDate && !isNaN(parsedDate.getTime())) {
                appState.date = isoDate;
                input.value = formatDateForDisplay(isoDate); // Display in MM/DD/YYYY format
                input.style.borderColor = '#d1d5db'; // Reset to normal border
                input.style.backgroundColor = 'white';
                renderApp();
            } else {
                // Invalid date - show error styling
                input.style.borderColor = '#ef4444'; // Red border
                input.style.backgroundColor = '#fef2f2'; // Light red background
                setTimeout(() => {
                    input.style.borderColor = '#d1d5db';
                    input.style.backgroundColor = 'white';
                }, 2000);
            }
        }

        // Helper function to format date for display (MM/DD/YYYY)
        function formatDateForDisplay(isoDate) {
            if (!isoDate) return '';
            const parts = isoDate.split('-');
            if (parts.length !== 3) return isoDate;

            const year = parts[0];
            const month = parts[1];
            const day = parts[2];

            return `${month}/${day}/${year}`;
        }

        function toggleNoProductionDateRange() {
            appState.showNoProductionDateRange = !appState.showNoProductionDateRange;
            renderApp();
        }

        function addNoProductionRecord() {
            try {
                // Validate date range
                const startDate = new Date(appState.noProductionStartDate);
                const endDate = new Date(appState.noProductionEndDate);

                if (endDate < startDate) {
                    appState.error = 'End date must be after start date.';
                    renderApp();
                    setTimeout(() => { appState.error = null; renderApp(); }, 3000);
                    return;
                }

                // Format dates as M/D/YY
                const formatDate = (date) => {
                    const month = date.getMonth() + 1;
                    const day = date.getDate();
                    const year = date.getFullYear().toString().slice(-2);
                    return `${month}/${day}/${year}`;
                };

                const startStr = formatDate(startDate);
                const endStr = formatDate(endDate);
                const dateRange = startDate.getTime() === endDate.getTime() ? 
                    `${startStr} - NO PRODUCTION` : 
                    `${startStr}-${endStr} - NO PRODUCTION`;

                // Create special no production record
                const noProductionRecord = {
                    id: Date.now(),
                    date: appState.noProductionStartDate, // Use start date for sorting
                    isNoProduction: true,
                    weekRange: dateRange,
                    aggregateName: dateRange,
                    aggregateType: 'No Production',
                    finenessModulus: 'N/A',
                    decant: 'N/A',
                    totalWeight: 'N/A',
                    washedWeight: 'N/A',
                    sieveResults: {}
                };

                // Save to repository
                const repository = loadFromStorage('testRepository', []);
                repository.push(noProductionRecord);
                saveToStorage('testRepository', repository);

                // Hide the date range selector and show success message
                appState.showNoProductionDateRange = false;
                appState.successMessage = `No production record added for ${dateRange}`;
                renderApp();
                setTimeout(() => { appState.successMessage = null; renderApp(); }, 3000);
            } catch (error) {
                console.error('Error adding no production record:', error);
                appState.error = 'Error adding no production record. Please try again.';
                renderApp();
                setTimeout(() => { appState.error = null; renderApp(); }, 3000);
            }
        }

        function loadDefaultTests() {
            const availableDefaults = appState.defaultAggregates.filter(name => appState.aggregates[name]);
            if (availableDefaults.length === 0) {
                availableDefaults.push(...Object.keys(appState.aggregates).slice(0, 4));
            }

            appState.activeTests = availableDefaults.map((name, index) => ({
                id: Date.now() + index,
                aggregateName: name,
                date: new Date().toISOString().split('T')[0],
                sieveData: appState.aggregates[name].sieves.map(sieve => ({
                    ...sieve, weightRetained: '', percentRetained: 0, cumulativeRetained: 0, percentPassing: 100
                })),
                washedWeight: 0
            }));
            renderApp();
        }

        function updateActiveTestsForAggregate(aggregateName) {
            appState.activeTests = appState.activeTests.map(test => {
                if (test.aggregateName === aggregateName) {
                    const newSieveData = appState.aggregates[aggregateName].sieves.map(sieve => {
                        const existingSieve = test.sieveData.find(s => s.name === sieve.name);
                        return {
                            ...sieve,
                            weightRetained: existingSieve ? existingSieve.weightRetained : '',
                        };
                    });
                    return { ...test, sieveData: calculateTestData(newSieveData) };
                }
                return test;
            });
        }

        // ===== ADMIN FUNCTIONS =====
        function handleToggleDefault(aggName) {
            const defaults = new Set(appState.defaultAggregates);
            if (defaults.has(aggName)) {
                defaults.delete(aggName);
            } else {
                defaults.add(aggName);
            }
            appState.defaultAggregates = Array.from(defaults);
            saveToStorage('defaultTests', appState.defaultAggregates);
            renderApp();
        }

        function handleAddAggregate() {
            if (!appState.newAggregateName.trim()) return alert('Please enter an aggregate name');
            if (appState.aggregates[appState.newAggregateName]) return alert('An aggregate with this name already exists');

            let newSieves = [];
            if (appState.importFromExisting && appState.aggregates[appState.importFromExisting]) {
                newSieves = JSON.parse(JSON.stringify(appState.aggregates[appState.importFromExisting].sieves));
            } else {
                newSieves = (appState.newAggregateType === 'Fine' ? DEFAULT_AGGREGATES['Concrete Sand'] : DEFAULT_AGGREGATES['Keystone #7']).sieves;
            }

            appState.aggregates[appState.newAggregateName] = {
                type: appState.importFromExisting ? appState.aggregates[appState.importFromExisting].type : appState.newAggregateType,
                sieves: newSieves
            };
            saveToStorage('aggregateConfigs', appState.aggregates);

            appState.editingAggregate = appState.newAggregateName;
            appState.showAddForm = false;
            appState.newAggregateName = '';
            appState.newAggregateType = 'Fine';
            appState.importFromExisting = '';
            renderApp();
        }

        function handleUpdateSieve(aggName, sieveIndex, field, value) {
            const val = value.trim();
            const numVal = parseFloat(val);
            appState.aggregates[aggName].sieves[sieveIndex][field] = val === '-' || val === '' || isNaN(numVal) ? '-' : numVal;
            saveToStorage('aggregateConfigs', appState.aggregates);
            updateActiveTestsForAggregate(aggName);
            renderApp();
        }

        function handleDeleteSieve(aggName, sieveIndex) {
            appState.aggregates[aggName].sieves.splice(sieveIndex, 1);
            saveToStorage('aggregateConfigs', appState.aggregates);
            updateActiveTestsForAggregate(aggName);
            renderApp();
        }

        function handleUpdateAggregateType(aggName, type) {
            appState.aggregates[aggName].type = type;
            saveToStorage('aggregateConfigs', appState.aggregates);
            updateActiveTestsForAggregate(aggName);
            renderApp();
        }

        function handleUpdateMaxDecant(aggName, value) {
            const val = value.trim();
            const numVal = parseFloat(val);
            appState.aggregates[aggName].maxDecant = val === '' || isNaN(numVal) ? null : numVal;
            saveToStorage('aggregateConfigs', appState.aggregates);
            renderApp();
        }

        function handleDeleteAggregate(name) {
            delete appState.aggregates[name];
            saveToStorage('aggregateConfigs', appState.aggregates);
            if (appState.editingAggregate === name) appState.editingAggregate = null;
            appState.confirmingDelete = null;
            renderApp();
        }

        function addSieveToAggregate(aggName, sieveName) {
            const newSieve = {
                name: sieveName,
                size: STANDARD_SIEVES[sieveName],
                c33Lower: '-',
                c33Upper: '-'
            };
            appState.aggregates[aggName].sieves.push(newSieve);

            const panSieve = appState.aggregates[aggName].sieves.find(s => s.name === 'Pan');
            const otherSieves = appState.aggregates[aggName].sieves.filter(s => s.name !== 'Pan');
            // Make the sort more robust to handle undefined sizes.
            otherSieves.sort((a, b) => (b.size === undefined ? -1 : b.size) - (a.size === undefined ? -1 : a.size));
            appState.aggregates[aggName].sieves = panSieve ? [...otherSieves, panSieve] : otherSieves;

            saveToStorage('aggregateConfigs', appState.aggregates);
            appState.showSieveSelector = null;
            updateActiveTestsForAggregate(aggName);
            renderApp();
        }

        // ===== REPOSITORY FUNCTIONS =====
        function handleQuickFilter(filter, value) {
            appState[filter] = value;
            if (filter.startsWith('filterDate')) {
                appState.filterDateTo = value;
            }
            renderApp();
        }

