        function handleEditRecord(recordId) {
            const records = loadFromStorage('testRepository', []);
            const record = records.find(r => r.id === recordId);
            if (record) {
                // Create a deep copy for editing
                appState.editingRecord = JSON.parse(JSON.stringify(record));
                appState.showEditModal = true;
                renderApp();
            }
        }

        function handleSaveEditedRecord() {
            if (!appState.editingRecord) return;

            const records = loadFromStorage('testRepository', []);
            const index = records.findIndex(r => r.id === appState.editingRecord.id);

            if (index !== -1) {
                // Recalculate derived values
                if (appState.editingRecord.fullSieveData) {
                    appState.editingRecord.fullSieveData = calculateTestData(appState.editingRecord.fullSieveData);

                    // Update sieve results
                    appState.editingRecord.sieveResults = {};
                    appState.editingRecord.fullSieveData.forEach(sieve => {
                        if (sieve.name !== 'Pan') {
                            appState.editingRecord.sieveResults[sieve.name] = parseFloat(sieve.percentPassing);
                        }
                    });

                    // Recalculate totals
                    const totalWeight = appState.editingRecord.fullSieveData.reduce((sum, sieve) => 
                        sum + (parseFloat(sieve.weightRetained) || 0), 0);
                    appState.editingRecord.totalWeight = totalWeight.toFixed(1);

                    // Recalculate decant
                    const washedWeight = parseFloat(appState.editingRecord.washedWeight) || 0;
                    appState.editingRecord.decant = totalWeight > 0 ? 
                        parseFloat(((totalWeight - washedWeight) / totalWeight * 100).toFixed(2)) : 0;

                    // Recalculate fineness modulus for fine aggregates
                    if (appState.editingRecord.aggregateType === 'Fine') {
                        const fmSieves = ['#4', '#8', '#16', '#30', '#50', '#100'];
                        let cumulativePercent = 0;
                        fmSieves.forEach(sieveName => {
                            const sieve = appState.editingRecord.fullSieveData.find(s => s.name === sieveName);
                            if (sieve) {
                                cumulativePercent += parseFloat(sieve.cumulativeRetained) || 0;
                            }
                        });
                        appState.editingRecord.finenessModulus = parseFloat((cumulativePercent / 100).toFixed(2));
                    }
                }

                records[index] = appState.editingRecord;
                saveToStorage('testRepository', records);

                appState.editingRecord = null;
                appState.showEditModal = false;
                appState.successMessage = 'Record updated successfully!';
                renderApp();

                setTimeout(() => {
                    appState.successMessage = null;
                    renderApp();
                }, 3000);
            }
        }

        function handleCancelEdit() {
            appState.editingRecord = null;
            appState.showEditModal = false;
            renderApp();
        }

        function handlePrintRecord(record) {
            // A flag to know if the modal was opened just for printing.
            const modalWasOpenedForPrint = !appState.viewingRecord;

            // Ensure the modal content is available for printing
            if (modalWasOpenedForPrint) {
                appState.viewingRecord = record;
                renderApp();
            }

            setTimeout(() => {
                document.body.classList.add('js-printing-record');

                window.addEventListener('afterprint', () => {
                    document.body.classList.remove('js-printing-record');

                    // If we opened the modal just for printing, close it afterwards.
                    if (modalWasOpenedForPrint) {
                        appState.viewingRecord = null;
                        renderApp();
                    }
                }, { once: true });

                window.print();
            }, 150); // A small delay to ensure the DOM is ready.
        }

        function handleExportRecord(record) {
            appState.showExportOptions = appState.showExportOptions === record.id ? null : record.id;
            renderApp();
        }

        function exportRecordAsCSV(record) {
            try {
                let csv = `Aggregate Gradation Analysis - Individual Record\n`;
                csv += `Date: ${formatDateForDisplay(record.date)}\n`;
                csv += `Aggregate: ${record.aggregateName}\n`;
                csv += `Type: ${record.aggregateType}\n`;
                csv += `Fineness Modulus: ${record.finenessModulus}\n`;
                csv += `Decant: ${record.decant}%\n\n`;
                csv += `Sieve,% Passing\n`;
                Object.entries(record.sieveResults || {}).forEach(([sieve, passing]) => {
                    csv += `${sieve},${passing.toFixed(1)}\n`;
                });

                const blob = new Blob([csv], { type: 'text/csv' });
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = `${record.aggregateName.replace(/[^a-z0-9]/gi, '')}_${record.date}_gradation.csv`;
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
                appState.showExportOptions = null;
                renderApp();
            } catch (error) {
                console.error('Export error:', error);
                alert('Error exporting record. Please try again.');
            }
        }

        function getFilteredRecords() {
            let filtered = dataService.getRepository();
            if (appState.filterAggregate) filtered = filtered.filter(r => r.aggregateName === appState.filterAggregate);
            if (appState.filterType) filtered = filtered.filter(r => r.aggregateType === appState.filterType);
            if (appState.filterDateFrom) filtered = filtered.filter(r => r.date >= appState.filterDateFrom);
            if (appState.filterDateTo) filtered = filtered.filter(r => r.date <= appState.filterDateTo);
            return filtered.sort((a, b) => new Date(b.date) - new Date(a.date));
        }

        function handleDeleteRecord(recordId) {
            dataService.deleteRecord(recordId);
            appState.confirmingDeleteRecord = null;
            renderApp();
        }

        function exportAllToCSV() {
            try {
                const filteredRecords = getFilteredRecords();
                const allSieveSizes = new Set();
                Object.values(appState.aggregates).forEach(agg => agg.sieves.forEach(s => s.name !== 'Pan' && allSieveSizes.add(s.name)));
                const sieveSizes = Array.from(allSieveSizes).sort((a, b) => (STANDARD_SIEVES[b] || 0) - (STANDARD_SIEVES[a] || 0));

                let csv = `Aggregate Gradation Analysis - Summary Report\n`;
                csv += `Export Date: ${new Date().toLocaleDateString()}\n`;
                csv += `Total Records: ${filteredRecords.length}\n\n`;
                csv += `Date,Aggregate,Type,F.M.,${sieveSizes.join(',')},Decant\n`;

                filteredRecords.forEach(record => {
                    csv += `${record.date},${record.aggregateName},${record.aggregateType},${record.finenessModulus},`;
                    csv += sieveSizes.map(size => record.sieveResults && record.sieveResults[size] !== undefined ? record.sieveResults[size].toFixed(1) : '-').join(',');
                    csv += `,${record.decant}\n`;
                });

                const blob = new Blob([csv], { type: 'text/csv' });
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = `gradation_summary_${new Date().toISOString().split('T')[0]}.csv`;
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            } catch (error) {
                console.error('Export error:', error);
                alert('Error exporting forms. Please try again.');
            }
        }

        function getStatusIndicator(record) {
            const aggregateConfig = appState.aggregates[record.aggregateName];
            if (!aggregateConfig || !record.sieveResults) {
                return '<span class="font-semibold">N/A</span>';
            }

            let failed = false;

            // Check sieve results
            for (const sieve of aggregateConfig.sieves) {
                if (sieve.name === 'Pan') continue;

                const lower = sieve.c33Lower;
                const upper = sieve.c33Upper;
                const passing = record.sieveResults[sieve.name];

                if (passing === undefined) continue;

                if ((lower !== '-' && passing < lower) || (upper !== '-' && passing > upper)) {
                    failed = true;
                    break;
                }
            }

            // Check decant limit
            if (!failed && aggregateConfig.maxDecant !== null && aggregateConfig.maxDecant !== undefined) {
                const decantValue = parseFloat(record.decant);
                if (!isNaN(decantValue) && decantValue > aggregateConfig.maxDecant) {
                    failed = true;
                }
            }

            if (failed) {
                return '<span class="font-semibold text-red-600">Fail</span>';
            } else {
                return '<span class="font-semibold text-green-600">Pass</span>';
            }
        }

