                                    <th class="border border-gray-300 px-1 py-0.5 text-xs" style="width: 10%;">Weight (g)</th>
                                    <th class="border border-gray-300 px-1 py-0.5 text-xs" style="width: 21%;">% Ret.</th>
                                    <th class="border border-gray-300 px-1 py-0.5 text-xs" style="width: 21%;">Cum. %</th>
                                    <th class="border border-gray-300 px-1 py-0.5 text-xs" style="width: 11%;">Pass %</th>
                                    <th class="border border-gray-300 px-1 py-0.5 text-xs" style="width: 11%;">Lower</th>
                                    <th class="border border-gray-300 px-1 py-0.5 text-xs" style="width: 11%;">Upper</th>
                                </tr>
                            </thead>
                            <tbody>
                                ${test.sieveData.map((row, sieveIndex) => `
                                    <tr class="${sieveIndex % 2 === 0 ? 'bg-white' : 'bg-gray-50'}">
                                        <td class="border border-gray-300 px-1 py-0 text-xs bg-gray-100">${row.name}</td>
                                        <td class="border border-gray-300 px-0.5 py-0">
                                            <input type="number" value="${row.weightRetained}" class="w-full px-0.5 py-0 text-xs border-0 bg-transparent focus:bg-white focus:border focus:border-blue-400 rounded" 
                                                   step="0.1" min="0" onfocus="this.select();"
                                                   data-test-index="${testIndex}" data-sieve-index="${sieveIndex}"
                                                   onkeydown="handleWeightKeydown(event)" 
                                                   onchange="handleWeightChange(${testIndex}, ${sieveIndex}, this.value)">
                                        </td>
                                        <td class="border border-gray-300 px-1 py-0 text-center text-xs bg-gray-100">${row.percentRetained}</td>
                                        <td class="border border-gray-300 px-1 py-0 text-center text-xs bg-gray-100">${row.cumulativeRetained}</td>
                                        <td class="border border-gray-300 px-1 py-0 text-center text-xs bg-gray-100">
                                            ${(() => {
                                                // Skip color coding if weightRetained is blank/empty
                                                const hasWeightData = row.weightRetained !== '' && row.weightRetained !== null && row.weightRetained !== undefined;
                                                if (!hasWeightData) {
                                                    return row.percentPassing;
                                                }

                                                const passing = parseFloat(row.percentPassing);
                                                const lower = row.c33Lower !== '-' ? parseFloat(row.c33Lower) : null;
                                                const upper = row.c33Upper !== '-' ? parseFloat(row.c33Upper) : null;

                                                let color = '';
                                                if (lower !== null && upper !== null && !isNaN(passing)) {
                                                    if (passing >= lower && passing <= upper) {
                                                        color = 'text-green-600 font-medium';
                                                    } else {
                                                        color = 'text-red-600 font-medium';
                                                    }
                                                }

                                                return `<span class="${color}">${row.percentPassing}</span>`;
                                            })()}
                                        </td>
                                        <td class="border border-gray-300 px-1 py-0 text-center text-xs bg-gray-100">${row.c33Lower === '-' ? '-' : row.c33Lower}</td>
                                        <td class="border border-gray-300 px-1 py-0 text-center text-xs bg-gray-100">${row.c33Upper === '-' ? '-' : row.c33Upper}</td>
                                    </tr>
                                `).join('')}
                            </tbody>
                            <tfoot>
                                <tr class="bg-yellow-100">
                                    <td class="border border-gray-300 px-1 py-0.5 font-semibold text-xs bg-gray-100">Total</td>
                                    <td class="border border-gray-300 px-1 py-0.5 text-center font-semibold text-xs bg-gray-100">${totalWeight.toFixed(1)}</td>
