                                            <input type="number" value="${test.washedWeight}" placeholder="grams" class="px-0.5 text-center text-xs font-semibold border-0 rounded focus:outline-none focus:ring-1 focus:ring-blue-400" 
                                                   style="width: 6.5em;" step="0.1" min="0" 
                                                   data-washed-index="${testIndex}"
                                                   onkeydown="handleWashedKeydown(event)"
                                                   onchange="handleWashedWeightChange(${testIndex}, this.value)">
