                                        <div class="flex items-center">
                                            <div class="p-3">
                                                <input type="checkbox" class="h-5 w-5 rounded border-gray-300 text-blue-600 focus:ring-blue-500" title="Set as default test on main page"
                                                    ${appState.defaultAggregates.includes(name) ? 'checked' : ''}
                                                    onchange='handleToggleDefault(${JSON.stringify(name)})'>
                                            </div>
                                            <div class="flex-grow p-3 cursor-pointer hover:bg-gray-50 transition-colors" onclick='appState.editingAggregate = ${JSON.stringify(name)}; renderApp();'>
                                                <span class="font-medium">${name}</span>
                                                <span class="ml-2 text-sm text-gray-500">${config.type}</span>
                                            </div>
                                            ${appState.confirmingDelete === name ? `
                                                <div class="flex"><button onclick='handleDeleteAggregate(${JSON.stringify(name)});' class="px-3 text-white bg-red-600 hover:bg-red-700 transition-colors">Confirm</button><button onclick="appState.confirmingDelete = null; renderApp();" class="px-3 text-gray-600 hover:bg-gray-100 border-l border-gray-300 transition-colors">Cancel</button></div>
                                            ` : `<button onclick='appState.confirmingDelete = ${JSON.stringify(name)}; renderApp();' class="px-4 py-3 text-red-600 hover:text-white hover:bg-red-600 border-l border-gray-300 transition-colors font-medium">Delete</button>`}
                                        </div>
                                    </div>`).join('')}
                                ${Object.keys(appState.aggregates).length === 0 ? '<p class="text-gray-500 text-center py-4">No aggregates configured. Click "Add New" to create one.</p>' : ''}
                            </div>
                        </div>
                        ${appState.editingAggregate && appState.aggregates[appState.editingAggregate] ? `
                            <div>
                                <h3 class="text-lg font-semibold mb-4">Edit: ${appState.editingAggregate}</h3>
                                <div class="mb-4">
                                    <label class="block text-sm font-medium mb-2">Type</label>
                                    <select onchange='handleUpdateAggregateType(${JSON.stringify(appState.editingAggregate)}, this.value)' class="w-full px-3 py-2 border rounded">
                                        <option value="Fine" ${appState.aggregates[appState.editingAggregate].type === 'Fine' ? 'selected' : ''}>Fine</option>
                                        <option value="Coarse" ${appState.aggregates[appState.editingAggregate].type === 'Coarse' ? 'selected' : ''}>Coarse</option>
                                    </select>
                                </div>
                                <div class="mb-4">
                                    <label class="block text-sm font-medium mb-2">Maximum Decant (%)</label>
                                    <input type="number" value="${appState.aggregates[appState.editingAggregate].maxDecant || ''}" 
                                           onchange='handleUpdateMaxDecant(${JSON.stringify(appState.editingAggregate)}, this.value)' 
                                           placeholder="e.g., 3.0" step="0.1" min="0" max="100"
                                           class="w-full px-3 py-2 border rounded">
                                    <p class="text-xs text-gray-500 mt-1">Leave blank for no decant limit</p>
                                </div>
                                <div>
                                    <div class="flex justify-between items-center mb-2">
                                        <label class="text-sm font-medium">Sieves</label>
                                        <button onclick='appState.showSieveSelector = ${JSON.stringify(appState.editingAggregate)}; renderApp();' class="px-2 py-1 bg-blue-600 text-white text-sm rounded hover:bg-blue-700">Add Sieve</button>
                                    </div>
                                    ${appState.showSieveSelector === appState.editingAggregate ? `
                                        <div class="mb-4 p-3 border-2 border-blue-500 rounded-lg bg-blue-50">
                                            <div class="flex items-center gap-2">
                                                <label class="text-sm font-medium">Select sieve to add:</label>
                                                <select class="px-3 py-1 border rounded text-sm" onchange='if(this.value) { addSieveToAggregate(${JSON.stringify(appState.editingAggregate)}, this.value); this.value = ""; }'>
                                                    <option value="">-- Select a sieve --</option>
                                                    ${Object.keys(STANDARD_SIEVES)
                                                        .filter(sieveName => sieveName !== 'Pan' && !appState.aggregates[appState.editingAggregate].sieves.some(s => s.name === sieveName))
                                                        .sort((a, b) => (STANDARD_SIEVES[b] || 0) - (STANDARD_SIEVES[a] || 0))
                                                        .map(sieveName => `<option value='${sieveName}'>${sieveName} (${STANDARD_SIEVES[sieveName]}mm)</option>`).join('')}
                                                </select>
                                                <button onclick="appState.showSieveSelector = null; renderApp();" class="px-3 py-1 bg-gray-400 text-white text-sm rounded hover:bg-gray-500">Cancel</button>
                                            </div>
                                        </div>` : ''}
                                    <div class="grid grid-cols-5 gap-1 mb-2 text-xs font-medium text-gray-600">
                                        <div>Name</div><div>Size (mm)</div><div>Lower %</div><div>Upper %</div><div></div>
                                    </div>
                                    <div class="space-y-2">${appState.aggregates[appState.editingAggregate].sieves.map((s, i) => createSieveEditor(s, i, appState.editingAggregate)).join('')}</div>
                                    <div class="mt-4 p-3 bg-gray-100 rounded text-sm">
                                        <p class="font-semibold mb-1">Tips:</p>
                                        <ul class="list-disc list-inside text-gray-700 space-y-1">
                                            <li>Sieves are sorted largest to smallest automatically.</li>
                                            <li>Pan should always be the last entry with size 0.</li>
                                            <li>ASTM C-33 limits represent acceptable passing percentages.</li>
                                            <li>Changes are saved automatically.</li>
                                        </ul>
                                    </div>
                                </div>
                            </div>` : ''}
                    </div>
                </div>`;
        }

        function renderRepositoryView() {
            const filteredRecords = getFilteredRecords();
            const allSieveSizes = new Set();
            Object.values(appState.aggregates).forEach(agg => agg.sieves.forEach(s => allSieveSizes.add(s.name)));
            const sieveSizes = Array.from(allSieveSizes).sort((a, b) => (STANDARD_SIEVES[b] || 0) - (STANDARD_SIEVES[a] || 0));

            return `
                <div class="bg-white rounded-lg shadow-md p-6">
                    <div class="flex justify-between items-center mb-6">
                        <h2 class="text-2xl font-bold">Test Records</h2>
                        <div class="flex gap-2">
                            ${filteredRecords.length > 0 ? `<button onclick="exportAllToCSV()" class="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">Export All (${filteredRecords.length})</button>` : ''}
                            <button onclick="appState.currentView = 'main'; renderApp();" class="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Back to Testing</button>
                        </div>
                    </div>
                    <div class="bg-gray-50 p-3 rounded-lg mb-4">
                        <div class="flex flex-wrap gap-4 items-end">
                            <div class="flex-grow" style="min-width: 180px;">
                                <label class="block text-xs font-medium mb-1">Aggregate</label>
                                <select onchange="appState.filterAggregate = this.value; renderApp();" class="w-full px-2 py-1 text-sm border rounded">
                                    <option value="">All Aggregates</option>
                                    ${Object.keys(appState.aggregates).map(name => `<option value="${name}" ${appState.filterAggregate === name ? 'selected' : ''}>${name}</option>`).join('')}
                                </select>
                            </div>
                            <div class="flex-grow" style="min-width: 120px;">
                                <label class="block text-xs font-medium mb-1">Type</label>
                                <select onchange="appState.filterType = this.value; renderApp();" class="w-full px-2 py-1 text-sm border rounded">
                                    <option value="">All Types</option>
                                    <option value="Fine" ${appState.filterType === 'Fine' ? 'selected' : ''}>Fine</option>
                                    <option value="Coarse" ${appState.filterType === 'Coarse' ? 'selected' : ''}>Coarse</option>
                                </select>
                            </div>
                            <div class="flex-grow" style="min-width: 150px;">
                                <label class="block text-xs font-medium mb-1">Date From</label>
                                <input type="date" value="${appState.filterDateFrom}" onchange="appState.filterDateFrom = this.value; renderApp();" class="w-full px-2 py-1 text-sm border rounded">
                            </div>
                            <div class="flex-grow" style="min-width: 150px;">
                                <label class="block text-xs font-medium mb-1">Date To</label>
                                <input type="date" value="${appState.filterDateTo}" onchange="appState.filterDateTo = this.value; renderApp();" class="w-full px-2 py-1 text-sm border rounded">
                            </div>
                            <div>
                                <button onclick="appState.filterAggregate = ''; appState.filterType = ''; appState.filterDateFrom = ''; appState.filterDateTo = ''; renderApp();" class="px-3 py-1 bg-gray-500 text-white text-sm rounded hover:bg-gray-600">Clear</button>
                            </div>
                        </div>
                    </div>
                    ${filteredRecords.length === 0 ? `<div class="text-center py-8 text-gray-500">No test records found.</div>` : `
                        <div class="flex justify-end items-center text-xs text-gray-500 mb-2">
                            <span>Sieve Columns:</span>
                            <div class="ml-2 text-center border rounded p-1 bg-gray-50" style="line-height: 1.1;">
                                <div class="font-medium border-b border-gray-300 pb-0.5">% Retained</div>
                                <div class="font-medium pt-0.5">% Passing</div>
                            </div>
                        </div>
                        <div class="overflow-x-auto">
                            <table class="w-full border-collapse text-sm">
                                <thead>
                                    <tr class="bg-gray-100">
                                        <th class="border border-gray-300 px-2 py-1 sticky left-0 bg-gray-100">Date</th>
                                        <th class="border border-gray-300 px-2 py-1">Aggregate</th>
                                        <th class="border border-gray-300 px-2 py-1">Type</th>
                                        <th class="border border-gray-300 px-2 py-1">F.M.</th>
                                        ${sieveSizes.map(size => `<th class="border border-gray-300 px-2 py-1">${size}</th>`).join('')}
                                        <th class="border border-gray-300 px-2 py-1">Decant</th>
                                        <th class="border border-gray-300 px-2 py-1">Status</th>
                                        <th class="border border-gray-300 px-2 py-1">Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    ${filteredRecords.map((record, index) => {
                                        // Special handling for no production records
                                        if (record.isNoProduction) {
                                            return `<tr class="${index % 2 === 0 ? 'bg-yellow-50' : 'bg-yellow-100'}">
                                                        <td class="border border-gray-300 px-2 py-1 font-medium text-gray-700" colspan="${6 + sieveSizes.length}">${record.weekRange}</td>
                                                        <td class="border border-gray-300 px-2 py-1 text-center">
                                                            ${appState.confirmingDeleteRecord === record.id ? `
                                                                <button onclick="handleDeleteRecord(${record.id})" class="text-white bg-red-600 hover:bg-red-700 px-2 py-0.5 rounded text-xs">Confirm</button>
                                                                <button onclick="appState.confirmingDeleteRecord = null; renderApp();" class="text-gray-600 hover:text-gray-800 underline text-xs ml-1">Cancel</button>
                                                            ` : `<button onclick="appState.confirmingDeleteRecord = ${record.id}; renderApp();" class="text-red-600 hover:text-red-800 underline text-xs">Delete</button>`}
                                                        </td>
                                                    </tr>`;
                                        }

                                        // Regular record rendering
                                        return `
                                        <tr class="${index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}">
                                            <td class="border border-gray-300 px-2 py-1 sticky left-0 bg-inherit"><button onclick='handleQuickFilter("filterDateFrom", ${JSON.stringify(record.date)})' class="text-blue-600 hover:text-blue-800 underline text-sm">${formatDateForDisplay(record.date)}</button></td>
                                            <td class="border border-gray-300 px-2 py-1"><button onclick='handleQuickFilter("filterAggregate", ${JSON.stringify(record.aggregateName)})' class="text-blue-600 hover:text-blue-800 underline text-sm">${record.aggregateName}</button></td>
                                            <td class="border border-gray-300 px-2 py-1"><button onclick='handleQuickFilter("filterType", ${JSON.stringify(record.aggregateType)})' class="text-blue-600 hover:text-blue-800 underline text-sm">${record.aggregateType}</button></td>
                                            <td class="border border-gray-300 px-2 py-1 text-center">${record.finenessModulus}</td>
                                            ${sieveSizes.map((size, index) => {
                                                const sieveResult = record.fullSieveData ? record.fullSieveData.find(s => s.name === size) : null;

                                                let percentPassing = '-';
                                                if (sieveResult) {
                                                    percentPassing = parseFloat(sieveResult.percentPassing).toFixed(0);
                                                } else if (record.sieveResults && record.sieveResults[size] !== undefined) {
                                                    percentPassing = record.sieveResults[size].toFixed(0);
                                                }

                                                let percentRetained = '-';
                                                if (sieveResult && sieveResult.percentRetained !== undefined) {
                                                    percentRetained = parseFloat(sieveResult.percentRetained).toFixed(0);
                                                } else if (record.sieveResults) {
                                                    // Calculate retained on the fly for older records
                                                    let priorPassing = 100.0;
                                                    // Find the next largest sieve size in the master list that has a result
                                                    for (let i = index - 1; i >= 0; i--) {
                                                        const priorSieveSize = sieveSizes[i];
                                                        if (record.sieveResults[priorSieveSize] !== undefined) {
                                                            priorPassing = record.sieveResults[priorSieveSize];
                                                            break;
                                                        }
                                                    }

                                                    if (size === 'Pan') {
                                                        percentRetained = priorPassing.toFixed(0);
                                                    } else if (record.sieveResults[size] !== undefined) {
                                                        const currentPassing = record.sieveResults[size];
                                                        percentRetained = (priorPassing - currentPassing).toFixed(0);
                                                    }
                                                }

                                                // Check if this sieve has defined limits for this aggregate
                                                const aggregateConfig = appState.aggregates[record.aggregateName];
                                                const sieveConfig = aggregateConfig ? aggregateConfig.sieves.find(s => s.name === size) : null;
                                                const hasDefinedLimits = sieveConfig && sieveConfig.c33Lower !== '-' && sieveConfig.c33Upper !== '-';

                                                if (size === 'Pan' || !hasDefinedLimits) {
                                                    percentPassing = '-';
                                                }

                                                if (percentRetained === '-' && percentPassing === '-') {
                                                    return `<td class="border border-gray-300 px-2 py-1 text-center">-</td>`;
                                                }

                                                return `<td class="border border-gray-300 p-0 text-center text-xs align-middle">
                                                            <div style="line-height: 1;">
                                                                <div class="border-b border-gray-300 px-1 py-0.5">${percentRetained}</div>
                                                                <div class="px-1 py-0.5">${(() => {
                                                                    if (percentPassing === '-') return percentPassing;

                                                                    const passing = parseFloat(percentPassing);
                                                                    const lower = sieveConfig && sieveConfig.c33Lower !== '-' ? parseFloat(sieveConfig.c33Lower) : null;
                                                                    const upper = sieveConfig && sieveConfig.c33Upper !== '-' ? parseFloat(sieveConfig.c33Upper) : null;

                                                                    let color = '';
                                                                    if (lower !== null && upper !== null && !isNaN(passing)) {
                                                                        if (passing >= lower && passing <= upper) {
                                                                            color = 'text-green-600 font-medium';
                                                                        } else {
                                                                            color = 'text-red-600 font-medium';
                                                                        }
                                                                    }

                                                                    return `<span class="${color}">${percentPassing}</span>`;
                                                                })()}</div>
                                                            </div>
                                                        </td>`;
                                            }).join('')}
                                            <td class="border border-gray-300 px-2 py-1 text-center">
                                                ${(() => {
                                                    const decantDisplay = !isNaN(parseFloat(record.decant)) ? parseFloat(record.decant).toFixed(2) : record.decant;
                                                    const aggregateConfig = appState.aggregates[record.aggregateName];

                                                    if (aggregateConfig && aggregateConfig.maxDecant !== null && aggregateConfig.maxDecant !== undefined) {
                                                        const decantValue = parseFloat(record.decant);
                                                        if (!isNaN(decantValue)) {
                                                            const color = decantValue <= aggregateConfig.maxDecant ? 'text-green-600 font-medium' : 'text-red-600 font-medium';
                                                            return `<span class="${color}">${decantDisplay}</span>`;
                                                        }
                                                    }

                                                    return decantDisplay;
                                                })()}
                                            </td>
                                            <td class="border border-gray-300 px-2 py-1 text-center">${getStatusIndicator(record)}</td>
                                            <td class="border border-gray-300 px-2 py-1 text-center">
                                                <div class="flex gap-1 justify-center flex-wrap">
                                                    <button onclick='handleViewRecord(${JSON.stringify(record)})' class="text-blue-600 hover:text-blue-800 underline text-xs">View</button>
                                                    <button onclick='handleEditRecord(${record.id})' class="text-orange-600 hover:text-orange-800 underline text-xs">Edit</button>
                                                    <button onclick='handlePrintRecord(${JSON.stringify(record)})' class="text-green-600 hover:text-green-800 underline text-xs">Print</button>
                                                    <button onclick='handleExportRecord(${JSON.stringify(record)})' class="text-purple-600 hover:text-purple-800 underline text-xs">Export</button>
                                                    ${appState.confirmingDeleteRecord === record.id ? `
                                                        <button onclick="handleDeleteRecord(${record.id})" class="text-white bg-red-600 hover:bg-red-700 px-2 py-0.5 rounded text-xs">Confirm</button>
                                                        <button onclick="appState.confirmingDeleteRecord = null; renderApp();" class="text-gray-600 hover:text-gray-800 underline text-xs">Cancel</button>
                                                    ` : `<button onclick="appState.confirmingDeleteRecord = ${record.id}; renderApp();" class="text-red-600 hover:text-red-800 underline text-xs">Delete</button>`}
                                                    ${appState.showExportOptions === record.id ? `
                                                        <div class="absolute bg-white border shadow-lg rounded p-2 mt-6 z-10 text-left">
                                                            <button onclick='exportRecordAsCSV(${JSON.stringify(record)})' class="block w-full text-left px-2 py-1 hover:bg-gray-100 text-xs">Export as Excel/CSV</button>
                                                            <button onclick='handlePrintRecord(${JSON.stringify(record)})' class="block w-full text-left px-2 py-1 hover:bg-gray-100 text-xs">Export as PDF (Print)</button>
                                                        </div>
                                                    ` : ''}
                                                </div>
                                            </td>
                                        </tr>`;
                                    }).join('')}
                                </tbody>
                            </table>
                        </div>`}
                </div>

                ${appState.showEditModal && appState.editingRecord ? renderEditModal() : ''}`;
        }

        function renderEditModal() {
            const record = appState.editingRecord;
            if (!record) return '';

            const aggregateOptions = Object.keys(appState.aggregates).map(name => 
                `<option value="${name}" ${name === record.aggregateName ? 'selected' : ''}>${name}</option>`
            ).join('');

            return `
                <div class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div class="bg-white rounded-lg shadow-lg max-w-4xl max-h-[90vh] overflow-auto p-6">
                        <div class="flex justify-between items-center mb-4">
                            <h2 class="text-xl font-bold">Edit Test Record</h2>
                            <button onclick="handleCancelEdit()" class="px-3 py-1 bg-gray-600 text-white rounded hover:bg-gray-700 transition-colors text-sm">
                                Cancel
                            </button>
                        </div>

                        <div class="grid grid-cols-2 gap-4 mb-4">
                            <div>
                                <label class="block text-sm font-medium mb-1">Date:</label>
                                <input type="date" value="${record.date}" 
                                       onchange="appState.editingRecord.date = this.value; renderApp();"
                                       class="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-blue-400">
                            </div>
                            <div>
                                <label class="block text-sm font-medium mb-1">Aggregate:</label>
                                <select onchange="appState.editingRecord.aggregateName = this.value; appState.editingRecord.aggregateType = appState.aggregates[this.value].type; renderApp();"
                                        class="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-blue-400">
                                    ${aggregateOptions}
                                </select>
                            </div>
                        </div>

                        <div class="grid grid-cols-2 gap-4 mb-4">
                            <div>
                                <label class="block text-sm font-medium mb-1">Washed Weight (g):</label>
                                <input type="number" step="0.1" value="${record.washedWeight}" 
                                       onchange="appState.editingRecord.washedWeight = this.value; renderApp();"
                                       class="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-blue-400">
                            </div>
                            <div class="bg-gray-50 p-3 rounded">
                                <div class="text-sm text-gray-600">Type: ${record.aggregateType}</div>
                            </div>
                        </div>

                        ${record.fullSieveData && record.fullSieveData.length > 0 ? `
                            <h3 class="text-lg font-semibold mb-3">Sieve Data</h3>
                            <div class="overflow-x-auto mb-4">
                                <table class="w-full border-collapse text-sm">
                                    <thead>
                                        <tr class="bg-gray-100">
                                            <th class="border border-gray-300 px-2 py-1">Sieve Size</th>
                                            <th class="border border-gray-300 px-2 py-1">Weight Retained (g)</th>
                                            <th class="border border-gray-300 px-2 py-1">% Retained</th>
                                            <th class="border border-gray-300 px-2 py-1">% Passing</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        ${record.fullSieveData.map((sieve, index) => `
                                            <tr>
                                                <td class="border border-gray-300 px-2 py-1 font-medium">${sieve.name}</td>
                                                <td class="border border-gray-300 px-2 py-1">
                                                    <input type="number" step="0.1" value="${sieve.weightRetained || ''}" 
                                                           onchange="appState.editingRecord.fullSieveData[${index}].weightRetained = parseFloat(this.value) || 0; renderApp();"
                                                           class="w-full px-2 py-1 border border-gray-300 rounded text-center focus:outline-none focus:ring-1 focus:ring-blue-400">
                                                </td>
                                                <td class="border border-gray-300 px-2 py-1 text-center">${sieve.percentRetained ? parseFloat(sieve.percentRetained).toFixed(1) : '0.0'}%</td>
                                                <td class="border border-gray-300 px-2 py-1 text-center">${sieve.name === 'Pan' ? '-' : (sieve.percentPassing ? parseFloat(sieve.percentPassing).toFixed(1) : '100.0') + '%'}</td>
                                            </tr>
                                        `).join('')}
                                    </tbody>
                                </table>
                            </div>
                        ` : ''}

                        <div class="flex gap-2 pt-4">
                            <button onclick="handleSaveEditedRecord()" class="flex-1 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors">
                                Save Changes
                            </button>
                            <button onclick="handleCancelEdit()" class="flex-1 px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500 transition-colors">
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            `;
        }

        function renderConfigureDefaults() {
            return `
                <div class="bg-white rounded-lg shadow-md p-6">
                    <div class="flex justify-between items-center mb-6">
                        <h2 class="text-2xl font-bold">Configure Default Aggregates</h2>
                        <button onclick="appState.currentView = 'main'; renderApp();" class="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Cancel</button>
                    </div>
                    <p class="text-gray-600 mb-4">Select which aggregates should appear on the main page by default. You can select up to 8.</p>
                    ${Object.keys(appState.aggregates).length === 0 ? `<div class="text-center py-8"><p class="text-gray-500 mb-4">No aggregates configured yet.</p><p class="text-gray-500">Please configure aggregates first before setting defaults.</p></div>` : `
                        <div class="mb-6">
                            <div class="text-sm text-gray-500 mb-2">Selected: ${appState.selectedDefaults.length} / 8</div>
                            <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
                                ${Object.keys(appState.aggregates).sort().map(aggregateName => `
                                    <label class="border rounded-lg p-3 cursor-pointer transition-colors ${appState.selectedDefaults.includes(aggregateName) ? 'border-blue-500 bg-blue-50' : 'border-gray-300 hover:border-gray-400'}" onchange="toggleDefaultAggregate('${aggregateName}')">
                                        <div class="flex items-center">
                                            <input type="checkbox" ${appState.selectedDefaults.includes(aggregateName) ? 'checked' : ''} class="mr-3">
                                            <div>
                                                <div class="font-medium">${aggregateName}</div>
                                                <div class="text-sm text-gray-500">${appState.aggregates[aggregateName].type}</div>
                                            </div>
                                        </div>
                                    </label>`).join('')}
                            </div>
                        </div>
                        ${appState.selectedDefaults.length > 0 ? `<div class="mb-6 p-4 bg-gray-50 rounded-lg"><h3 class="font-semibold mb-2">Selected Defaults (in order):</h3><div class="text-sm">${appState.selectedDefaults.map((name, index) => `${index + 1}. ${name}`).join(', ')}</div></div>` : ''}
                        <div class="flex justify-end gap-2">
                            <button onclick="appState.currentView = 'main'; renderApp();" class="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500">Cancel</button>
                            <button onclick="saveDefaults()" class="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Save Defaults</button>
                        </div>`}
                </div>`;
        }

        function renderViewRecordModal(record, sieveData) {
            if (!record) return '';

            return `
                <div class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-auto p-4">
                    <div class="bg-white rounded-lg p-6 max-w-7xl max-h-[90vh] overflow-y-auto w-full">
                        <div class="flex justify-between items-center mb-4 no-print">
                            <h3 class="text-xl font-semibold">${record.aggregateName} - ${formatDateForDisplay(record.date)}</h3>
                            <button onclick="appState.viewingRecord = null; renderApp();" class="text-gray-500 hover:text-gray-700 text-2xl"></button>
                        </div>
                        <div class="print-visible mb-4">
                            <h3 class="text-xl font-semibold text-center">${record.aggregateName} - ${formatDateForDisplay(record.date)}</h3>
                        </div>
                        <div class="grid grid-cols-1 lg:grid-cols-2 gap-6 record-view-grid">
                            <div>
                                <h4 class="text-lg font-semibold mb-3">Gradation Results</h4>
                                <div class="mb-4 text-sm grid grid-cols-2 gap-x-4">
                                    <p><strong>Type:</strong> ${record.aggregateType}</p>
                                    <p><strong>Total Weight:</strong> ${record.totalWeight}g</p>
                                    <p><strong>F.M.:</strong> ${record.finenessModulus}</p>
                                    <p><strong>Washed Weight:</strong> ${record.washedWeight}g</p>
                                    <p><strong>Decant:</strong> 
                                        ${(() => {
                                            const decantDisplay = !isNaN(parseFloat(record.decant)) ? parseFloat(record.decant).toFixed(2) : record.decant;
                                            const aggregateConfig = appState.aggregates[record.aggregateName];

                                            if (aggregateConfig && aggregateConfig.maxDecant !== null && aggregateConfig.maxDecant !== undefined) {
                                                const decantValue = parseFloat(record.decant);
                                                if (!isNaN(decantValue)) {
                                                    const color = decantValue <= aggregateConfig.maxDecant ? 'text-green-600 font-medium' : 'text-red-600 font-medium';
                                                    return `<span class="${color}">${decantDisplay}%</span>`;
                                                }
                                            }

                                            return `${decantDisplay}%`;
                                        })()}
                                    </p>
                                </div>
                                <table class="w-full border-collapse text-xs">
                                    <thead><tr class="bg-gray-100"><th class="border p-1 text-left">Sieve</th><th class="border p-1">Weight (g)</th><th class="border p-1">% Ret.</th><th class="border p-1">Cum. %</th><th class="border p-1">Pass %</th><th class="border p-1">Lower</th><th class="border p-1">Upper</th></tr></thead>
                                    <tbody>${(sieveData || []).map((row, i) => `<tr class="${i % 2 === 0 ? 'bg-white' : 'bg-gray-50'}"><td class="border p-1">${row.name}</td><td class="border p-1">${row.weightRetained}</td><td class="border p-1">${row.percentRetained}</td><td class="border p-1">${row.cumulativeRetained}</td><td class="border p-1">${row.percentPassing}</td><td class="border p-1">${row.c33Lower}</td><td class="border p-1">${row.c33Upper}</td></tr>`).join('')}</tbody>
                                </table>
                            </div>
                            <div>
                                <h4 class="text-lg font-semibold mb-3">Gradation Curve</h4>
                                <div id="viewRecordChart" class="w-full h-96 bg-gray-50 border rounded"></div>
                            </div>
                        </div>
                        <div class="mt-6 flex gap-2 justify-end no-print">
                            <button onclick='handlePrintRecord(${JSON.stringify(record)})' class="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">Print</button>
                            <button onclick='exportRecordAsCSV(${JSON.stringify(record)})' class="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Export CSV</button>
                            <button onclick="appState.viewingRecord = null; renderApp();" class="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500">Close</button>
                        </div>
                    </div>
                </div>`;
        }

        function renderPrintModal() {
            if (!appState.showPrintModal) return '';

            const aggregatesToPrint = Object.values(appState.aggregates);

            return `
                <div class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 no-print">
                    <div class="bg-white rounded-lg p-6 max-w-xl max-h-[80vh] overflow-y-auto">
                        <div class="flex justify-between items-center mb-4">
                            <h3 class="text-lg font-semibold">Print Blank Forms</h3>
                            <button onclick="appState.showPrintModal = false; renderApp();" class="text-gray-500 hover:text-gray-700 text-2xl"></button>
                        </div>
                        <div class="text-gray-600 mb-4">
                            <p>Ready to print ${appState.activeTests.length} blank test forms.</p>
                            <p class="text-sm mt-1">This will generate ${Math.ceil(appState.activeTests.length / 5)} ${Math.ceil(appState.activeTests.length / 5) === 1 ? 'page' : 'pages'} with up to 5 forms per page.</p>
                            ${appState.activeTests.length > 5 ? `<p class="text-sm mt-2 font-medium">Forms to print:</p><ul class="text-sm mt-1 list-disc list-inside max-h-32 overflow-y-auto">${appState.activeTests.map(test => `<li>${test.aggregateName} - ${test.date}</li>`).join('')}</ul>` : ''}
                        </div>
                        <div class="flex justify-end gap-2">
                            <button onclick="appState.showPrintModal = false; renderApp();" class="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500">Cancel</button>
                            <button onclick="window.print(); appState.showPrintModal = false; renderApp();" class="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Print Forms</button>
                        </div>
                    </div>
                </div>
                ${renderPrintContent()}`;
        }

        function renderPrintContent() {
            const pagesCount = Math.ceil(appState.activeTests.length / 5);
            let printContent = '<div class="print-only">';
            for (let pageIndex = 0; pageIndex < pagesCount; pageIndex++) {
                printContent += '<div class="print-page"><div class="grid grid-cols-5 gap-4">';
                const testsOnPage = appState.activeTests.slice(pageIndex * 5, (pageIndex + 1) * 5);
                testsOnPage.forEach(test => {
                    const aggregate = appState.aggregates[test.aggregateName];
                    if (!aggregate) return;
                    printContent += `
                        <div class="print-form-small border-2 border-gray-800 p-1">
                            <h4 class="text-xs font-bold text-center mb-1">${test.aggregateName}</h4>
                            <div class="text-xs text-center mb-2">Date: ${formatDateForDisplay(test.date)}</div>
                            <table class="w-full border-collapse">
                                <thead><tr class="bg-gray-200"><th class="border border-gray-800 p-0.5 text-xs">Sieve</th><th class="border border-gray-800 p-0.5 bg-white text-xs">Weight</th></tr></thead>
                                <tbody>
                                    ${aggregate.sieves.map(sieve => `<tr><td class="border border-gray-800 p-0.5 text-center text-xs font-medium bg-gray-300">${sieve.name}</td><td class="border border-gray-800 p-2 bg-white"></td></tr>`).join('')}
                                    <tr><td class="border border-gray-800 p-0.5 font-bold bg-gray-400 text-xs">Washed:</td><td class="border border-gray-800 p-2 bg-white"></td></tr>
                                </tbody>
                            </table>
                        </div>`;
                });
                printContent += '</div></div>';
            }
            return printContent + '</div>';
        }

        // ===== MAIN RENDER FUNCTION =====
        function renderApp() {
            const app = document.getElementById('app');
            app.innerHTML = ''; // Clear previous content

            if (appState.currentView === 'admin') {
                app.innerHTML = renderAdminView();
            } else if (appState.currentView === 'repository') {
                app.innerHTML = renderRepositoryView();
            } else {
                app.innerHTML = renderMainView();
            }

            // Render modals
            app.innerHTML += renderPrintModal();

            if (appState.viewingRecord) {
                const record = appState.viewingRecord;
                let displayData = record.fullSieveData;
                const aggregateConfig = appState.aggregates[record.aggregateName];

                // Attempt to repair data for older records that may be missing sieve names/config
                if (displayData && aggregateConfig && displayData.length === aggregateConfig.sieves.length) {
                    displayData = displayData.map((sieveResult, index) => {
                        return { ...aggregateConfig.sieves[index], ...sieveResult };
                    });
                }

                if (!displayData && record.sieveResults && aggregateConfig) {
                    let previousPassing = 100;
                    displayData = aggregateConfig.sieves.map(sieve => {
                        const currentPassing = record.sieveResults[sieve.name] !== undefined ? record.sieveResults[sieve.name] : (sieve.name === 'Pan' ? 0 : 100);
                        const percentRetained = previousPassing - currentPassing;
                        const cumulativeRetained = 100 - currentPassing;
                        const weightRetained = (percentRetained / 100) * (parseFloat(record.totalWeight) || 0);
                        if (sieve.name !== 'Pan') previousPassing = currentPassing;
                        return { ...sieve, weightRetained: weightRetained.toFixed(1), percentRetained: percentRetained.toFixed(1), cumulativeRetained: cumulativeRetained.toFixed(1), percentPassing: currentPassing.toFixed(1) };
                    });
                }

                app.innerHTML += renderViewRecordModal(record, displayData || []);

                const chartContainer = document.getElementById('viewRecordChart');
                if (chartContainer && displayData) {
                    setTimeout(() => createSimpleChart(chartContainer, displayData), 50);
                }
            }
        }

        // ===== CONFIGURE DEFAULTS FUNCTIONS =====
        function toggleDefaultAggregate(aggregateName) {
            if (appState.selectedDefaults.includes(aggregateName)) {
                appState.selectedDefaults = appState.selectedDefaults.filter(name => name !== aggregateName);
            } else if (appState.selectedDefaults.length < 8) {
                appState.selectedDefaults.push(aggregateName);
            } else {
                alert('You can select up to 8 default aggregates');
            }
            renderApp();
        }

        function saveDefaults() {
            appState.defaultAggregates = [...appState.selectedDefaults];
            saveToStorage('defaultAggregates', appState.defaultAggregates);
            appState.currentView = 'main';
            loadDefaultTests();
            alert(`Default aggregates saved! ${appState.defaultAggregates.length} aggregates will appear on the main page.`);
        }

        // ===== PRINT FUNCTIONS =====
        function exportBlankForms() {
            try {
                let csv = `Aggregate Gradation Analysis - Blank Forms\nGenerated: ${new Date().toLocaleDateString()}\n\n`;
                appState.activeTests.forEach((test, index) => {
                    csv += `Form ${index + 1}: ${test.aggregateName}\nDate: ${test.date}\nType: ${appState.aggregates[test.aggregateName]?.type}\n\n`;
                    csv += `Sieve,Size (mm),Weight Retained (g),% Retained,Cumulative % Retained,% Passing,Lower Limit,Upper Limit\n`;
                    test.sieveData.forEach(sieve => {
                        csv += `${sieve.name},${sieve.size},,,,${sieve.c33Lower === '-' ? '' : sieve.c33Lower},${sieve.c33Upper === '-' ? '' : sieve.c33Upper}\n`;
                    });
                    csv += `\n\n`;
                });
                const blob = new Blob([csv], { type: 'text/csv' });
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = `blank_forms_${new Date().toISOString().split('T')[0]}.csv`;
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            } catch (error) {
                console.error('Export error:', error);
                alert('Error exporting data. Please try again.');
            }
        }

        // ===== INITIALIZATION =====
        function initializeApp() {
            console.log('Initializing app...');
            appState.aggregates = loadFromStorage('aggregateConfigs', DEFAULT_AGGREGATES);
            appState.savedRecords = loadFromStorage('testRepository', []);
            const loadedDefaults = loadFromStorage('defaultAggregates', ['Keystone #7', 'Kraemer 9/16"', '#9 Gravel (St. Croix)', 'Concrete Sand']);
            appState.defaultAggregates = loadedDefaults;
            appState.selectedDefaults = [...loadedDefaults];

            if (appState.activeTests.length === 0 && Object.keys(appState.aggregates).length > 0 && loadedDefaults.length > 0) {
                loadDefaultTests();
            }

            console.log('App state initialized:', appState);
            renderApp();
            console.log('App initialization complete!');

            // Fix existing records with incorrect F.M. values
            fixExistingFinenessModulus();
        }

        // Function to fix existing records with incorrect F.M. calculations
        function fixExistingFinenessModulus() {
            const records = JSON.parse(localStorage.getItem('testRepository') || '[]');
            let fixedCount = 0;

            records.forEach(record => {
                if (record.aggregateType === 'Fine' && record.fullSieveData) {
                    const currentFM = parseFloat(record.finenessModulus);

                    // Check if F.M. is incorrect (greater than 10 indicates likely error)
                    if (!isNaN(currentFM) && currentFM > 10) {
                        // Recalculate F.M. using the corrected function
                        const fmSieves = ['3/8"', '#4', '#8', '#16', '#30', '#50', '#100'];
                        let cumulativePercent = 0;

                        fmSieves.forEach(sieveName => {
                            const sieve = record.fullSieveData.find(s => s.name === sieveName);
                            if (sieve) {
                                cumulativePercent += parseFloat(sieve.cumulativeRetained) || 0;
                            }
                        });

                        const newFM = parseFloat((cumulativePercent / 100).toFixed(2));

                        console.log(`Fixing F.M. for ${record.aggregateName} (${record.date}): ${currentFM}  ${newFM}`);
                        record.finenessModulus = newFM;
                        fixedCount++;
                    }
                }
            });

            if (fixedCount > 0) {
                localStorage.setItem('testRepository', JSON.stringify(records));
                console.log(`Fixed ${fixedCount} records with incorrect F.M. values`);
            }
        }

        // Start the app when DOM is ready
        document.addEventListener('DOMContentLoaded', initializeApp);

    </script>
</body>
</html>
